mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM (SELECT o.order_num, o.order_date,
REPLACE(REPLACE(p.prod_name, '<', '&lt;'), '>', '&gt;') prod_name,
o.order_address1, o.order_address2, o.pay_quan, o.pay_price, o.receiver_name, o.receiver_phone, p.prod_num FROM product p JOIN orders o ON p.prod_num = o.prod_num
WHERE o.mem_num = #{mem_num}
ORDER BY o.order_num DESC) a)
WHERE rnum >= #{start} AND rnum <= #{end}
